// Filter rows that don't have 'Sent' status and have valid email
const filteredItems = [];

console.log(`Processing ${items.length} items from Google Sheets`);

for (const item of items) {
  const rowData = item.json;

  console.log('Row data:', JSON.stringify(rowData, null, 2));

  // Get the status - check multiple possible column names
  const emailStatus = (rowData['Status'] || rowData['Email Status'] || '').toString().toLowerCase().trim();

  // Get email and validate
  const email = (rowData['Email'] || '').toString().trim();

  // Get other required fields with proper fallbacks
  const name = (rowData['Name'] || '').toString().trim();
  const ticketId = (rowData['Ticket ID'] || '').toString().trim();

  console.log(`Processing row: Name=${name}, Email=${email}, Status=${emailStatus}, TicketID=${ticketId}`);

  // Only process if:
  // 1. Not already sent
  // 2. Has valid email
  // 3. Has name
  // 4. Has ticket ID
  if (emailStatus !== 'sent' && 
      email && 
      /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email) && 
      name && 
      ticketId) {

    // Calculate row number (items are 0-based, but sheets are 1-based with header)
    const rowNumber = items.indexOf(item) + 2;

    const ticketData = {
      row_number: rowNumber,
      ticket_id: ticketId,
      name: name,
      email: email.toLowerCase(),
      phone: (rowData['phone'] || rowData['Phone'] || 'Not provided').toString().trim(),
      event_name: rowData['Event Name'] || 'Your Event Name',
      event_date: rowData['Event Date'] || 'Event Date',
      event_location: rowData['Event Location'] || 'Event Location',
      created_at: new Date().toISOString()
    };

    // QR code payload with actual participant data
    const qrCodeData = `Name: ${ticketData.name} | Email: ${ticketData.email} | Phone: ${ticketData.phone} | Ticket: ${ticketData.ticket_id} | Event: ${ticketData.event_name}`;
    ticketData.qr_data = qrCodeData;

    console.log(`Adding to queue: ${ticketData.name} (${ticketData.email})`);
    filteredItems.push({ json: ticketData });
  } else {
    if (emailStatus === 'sent') {
      console.log(`Skipping ${name || 'Unknown'} - already sent`);
    } else if (!email || !/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email)) {
      console.log(`Skipping ${name || 'Unknown'} - invalid email: ${email}`);
    } else if (!name) {
      console.log(`Skipping - no name provided`);
    } else if (!ticketId) {
      console.log(`Skipping ${name} - no ticket ID`);
    }
  }
}

console.log(`Found ${filteredItems.length} rows to process for email sending`);
return filteredItems;

const qrCodeBinary = items[0].binary.data;
const ticketData = items[0].json;

console.log(`Preparing email data for: ${ticketData.name} (${ticketData.email})`);

return {
  json: {
    ...ticketData,
    qr_base64: qrCodeBinary.data,
    qr_mime_type: qrCodeBinary.mimeType
  }
};

