const emailSent = items[0].json.email || 'Unknown';
const ticketId = items[0].json.ticket_id || 'Unknown';
const participantName = items[0].json.name || 'Unknown';

// Check if email was successfully sent
if (items[0].error) {
  console.log(`âŒ FAILED: ${participantName} (${emailSent}) - ${items[0].error.message}`);
  return {
    json: {
      success: false,
      message: 'Email sending failed',
      participant_name: participantName,
      ticket_id: ticketId,
      email: emailSent,
      error: items[0].error.message,
      processed_at: new Date().toISOString()
    }
  };
} else {
  console.log(`âœ… SUCCESS: Ticket email sent to: ${participantName} (${emailSent})`);
  console.log(`   Ticket ID: ${ticketId}`);
  console.log(`   Status updated in sheet`);

  return {
    json: {
      success: true,
      message: 'Email sent and status updated successfully',
      participant_name: participantName,
      ticket_id: ticketId,
      email: emailSent,
      processed_at: new Date().toISOString()
    }
  };
}

// Add a small delay to avoid hitting email rate limits
const delayMs = 2000; // 2 seconds between emails
const ticketId = items[0].json.ticket_id || 'Unknown';
const participantName = items[0].json.name || 'Unknown';

console.log(`Waiting ${delayMs/1000} seconds before next email (${participantName} - ${ticketId})...`);

return new Promise(resolve => {
  setTimeout(() => {
    resolve(items);
  }, delayMs);
});
